CREATE TABLE IF NOT EXISTS `dir` (`id` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`name` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`owner` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`parent` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,`recycle` int(11) NOT NULL DEFAULT '0',`share` int(11) NOT NULL DEFAULT '0',`color` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `file` (`name` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,`size` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`owner` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`secret` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`id` varchar(256) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`realname` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`type` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`dir` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`recycle` int(11) NOT NULL DEFAULT '0',`share` int(11) NOT NULL DEFAULT '0',`color` varchar(32) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `setting` (`name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`value` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `user` (`name` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`pass` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`email` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`jointime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP) ENGINE=MyISAM DEFAULT CHARSET=latin1;
