CREATE TABLE IF NOT EXISTS `dir` (`id` VARCHAR(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`name` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`owner` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`parent` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,PRIMARY KEY (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=0 ;
CREATE TABLE IF NOT EXISTS `file` (`name` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,`size` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`owner` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`secret` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`id` varchar(256) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`realname` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`type` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`dir` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `setting` (`name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`value` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,PRIMARY KEY (`name`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;
