CREATE TABLE IF NOT EXISTS `user` (`name` varchar(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`pass` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`email` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,`jointime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,PRIMARY KEY (`name`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;